mapper.select:
SELECT * FROM (
select
s.id,s.check_status AS checkStatus,s.create_time as createTime,s.money,s.remarks,
s.salesman_id AS salesmanId,u.name as salesmanName,s.the_type as theType,s.order_id AS orderId,sz.name as schoolZoneName,v.name AS clazzName
from laky_finance_money_record s
RIGHT JOIN laky_organization_school_zone sz ON s.school_zone_id=sz.id AND sz.the_status=1
LEFT JOIN  laky_organization_parameter_value v ON s.clazz_id = v.id AND v.the_status!=0
LEFT JOIN laky_organization_user u ON s.salesman_id = u.id AND u.the_status!=0
where s.the_status !=0 and
s.school_zone_id IN

  #{schoolId}



    AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}


    AND s.school_zone_id IN

      #{schoolId}




  AND s.the_type = #{theType,jdbcType=INTEGER}


  AND s.check_status = #{checkStatus,jdbcType=INTEGER}


  AND s.create_time >= #{createTime[0],jdbcType=TIMESTAMP}
  AND s.create_time <= #{createTime[1],jdbcType=TIMESTAMP}

ORDER BY s.create_time DESC
) t

  ORDER BY t.${columnName} ${columnOrder}

mapper.insert:
insert into laky_finance_money_record (id, school_zone_id, create_time, 
  money, the_type, salesman_id,
  user_id, the_status, check_status,remarks,clazz_id
  )
values (#{id,jdbcType=INTEGER}, #{schoolZoneId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{money,jdbcType=TINYINT}, #{theType,jdbcType=TINYINT}, #{salesmanId,jdbcType=INTEGER},
  #{userId,jdbcType=INTEGER}, #{theStatus,jdbcType=TINYINT}, #{checkStatus,jdbcType=TINYINT},#{remarks,jdbcType=VARCHAR},#{clazzId,jdbcType=INTEGER}
  )
