mapper.select:
select

from laky_logistics_goods_repository
where goods_id = #{id,jdbcType=INTEGER} AND school_zone_id = #{schoolZoneId,jdbcType=INTEGER}

select gr.id,gr.consume_amount AS consumeAmount,gr.last_amount AS lastAmount,v.name as clazzName, s.name AS schoolZoneId,g.name AS goodsName from laky_logistics_goods_repository gr RIGHT JOIN laky_organization_school_zone s ON gr.school_zone_id = s.id AND s.the_status!=0 LEFT JOIN laky_logistics_goods g ON gr.goods_id = g.id AND g.the_status!=0 LEFT JOIN laky_organization_parameter_value v ON g.clazz_id= v.id where gr.school_zone_id IN
#{schoolId}
AND g.name LIKE CONCAT('%',#{goodsName,jdbcType="INTEGER"},'%')
AND gr.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}
AND gr.school_zone_id IN
#{schoolId}

mapper.update:
update laky_logistics_goods_repository
set goods_id = #{goodsId,jdbcType=INTEGER},
  school_zone_id = #{schoolZoneId,jdbcType=INTEGER},
  last_amount = #{lastAmount,jdbcType=INTEGER},
  consume_amount = #{consumeAmount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
