mapper.select:
select gr.id,gr.consume_amount AS consumeAmount,gr.last_amount AS lastAmount,
s.name AS schoolZoneId,g.name AS goodsId
from laky_logistics_goods_repository gr
RIGHT JOIN laky_organization_school_zone s ON gr.school_zone_id = s.id AND s.the_status!=0
LEFT JOIN laky_logistics_goods g ON gr.goods_id = g.id AND g.the_status!=0
where gr.id = #{id,jdbcType=INTEGER}

  AND g.name LIKE CONCAT('%',#{goodsName,jdbcType="INTEGER"},'%')


  AND s.school_zone_id IN

    #{schoolId}




    AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}


    AND s.school_zone_id IN

      #{schoolId}

mapper.update:
update laky_logistics_goods_repository
set goods_id = #{goodsId,jdbcType=INTEGER},
  school_zone_id = #{schoolZoneId,jdbcType=INTEGER},
  last_amount = #{lastAmount,jdbcType=INTEGER},
  consume_amount = #{consumeAmount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
