SELECT s.id , s.name ,s.sex ,s.birthday AS birthday,s.phone, s.the_type AS theType, s.remarks ,s.create_time AS createTime, sz.name as schoolZoneName,sz.id as schoolZoneId, v.name AS contactName,v.id as contactName, u.name AS ownerName,u.id as ownerId, a.name AS sourceName, a.id AS sourceId, i.intentionCourseName,i.intentionCourseId from laky_supply_student s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 LEFT JOIN laky_organization_parameter_value v ON s.contact_id = v.id AND v.the_status!=0 LEFT JOIN laky_organization_user u ON s.owner_id = u.id AND u.the_status!=0 LEFT JOIN laky_supply_market_activity a ON s.source_id = a.id AND a.the_status!=0 LEFT JOIN (SELECT group_concat(c.name) as intentionCourseName,group_concat(i.course_id) as intentionCourseId,i.student_id FROM laky_supply_intention_course i LEFT JOIN laky_teach_course c ON i.course_id = c.id AND c.the_status!=0 GROUP BY i.student_id) i ON i.student_id = s.id where s.id =#{id,jdbcType=INTEGER} AND s.the_status != 0 AND s.the_type !=9
AND s.school_zone_id IN
#{schoolId}

SELECT s.id , s.name ,s.sex ,s.birthday AS birthday,s.phone, s.the_type AS theType, s.remarks ,s.create_time AS createTime, sz.name as schoolZoneName, v.name AS contactId, u.name AS ownerId, a.name AS sourceId, (SELECT group_concat(c.name) FROM laky_supply_intention_course i RIGHT JOIN laky_teach_course c ON i.course_id = c.id AND c.the_status!=0 WHERE i.student_id=s.id ) AS intentionCourseName from laky_supply_student s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 LEFT JOIN laky_organization_parameter_value v ON s.contact_id = v.id AND v.the_status!=0 LEFT JOIN laky_organization_user u ON s.owner_id = u.id AND u.the_status!=0 LEFT JOIN laky_supply_market_activity a ON s.source_id = a.id AND a.the_status!=0 where s.the_status !=0 AND s.the_type !=9
AND (s.`name` LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%') OR s.pinyin LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%') OR s.phone LIKE CONCAT('%',#{name,jdbcType=CHAR},'%'))
AND s.sex = #{sex,jdbcType=INTEGER}
AND s.birthday =#{birthday,jdbcType=DATE}
AND s.phone = #{phone,jdbcType=CHAR}
AND s.remarks =#{remarks,jdbcType=VARCHAR}
AND s.school_zone_id IN
#{schoolId}
AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}
AND s.school_zone_id IN
#{schoolId}
ORDER BY s.create_time DESC
