mapper.select:
select s.id,s.invite_time AS inviteTime,s.the_status AS theStatus,s.remarks, sz.name AS schoolZoneName,st.name AS studentId,u.name AS userId FROM laky_supply_invite s RIGHT JOIN laky_organization_school_zone sz ON s.school_zone_id = sz.id AND sz.the_status!=0 LEFT JOIN laky_supply_student st ON s.student_id = st.id AND st.the_status!=0 LEFT JOIN laky_organization_user u ON s.user_id = u.id AND u.the_status!=0 where s.the_status !=0
AND u.name LIKE CONCAT('%',#{userId,jdbcType=VARCHAR},'%')
AND (st.name LIKE CONCAT('%',#{studentName,jdbcType=VARCHAR},'%') OR st.pinyin LIKE CONCAT('%',#{pinyin,jdbcType=VARCHAR},'%') OR st.phone LIKE CONCAT('%',#{phone,jdbcType=CHAR},'%'))
AND s.school_zone_id IN
#{schoolId}
AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}
AND s.school_zone_id IN
#{schoolId}
