mapper.select:
SELECT s.max_count AS maxCount ,s.id AS id,s.name AS name,s.sort AS sort ,sz.name as schoolZoneName,sz.id AS schoolZoneId from laky_teach_room s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 where s.id =#{id,jdbcType=INTEGER} AND s.the_status !=0
AND s.school_zone_id IN
#{schoolId}

SELECT s.*,sz.name as schoolZoneName,sz.id AS schoolZoneId from laky_teach_room s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 where s.the_status !=0
AND s.`name` LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')
AND s.school_zone_id IN
#{schoolId}
AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}
AND s.school_zone_id IN
#{schoolId}
ORDER BY s.sort

mapper.update:
update laky_teach_room
set name = #{name,jdbcType=VARCHAR},
  max_count = #{maxCount,jdbcType=INTEGER},
  sort = #{sort,jdbcType=INTEGER},
  school_zone_id = #{schoolZoneId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
