SELECT s.id AS id,s.end_date AS endDate,s.start_date AS startDate,s.name AS name,s.create_time AS createTime,s.remarks AS remarks,r.id as roomId, r.`name` AS roomName, c.`name` AS courseName,u1.id as mainTeacherId,u1.name AS mainTeacherName,u.`name` AS teacherName,sz.name as schoolZoneName from laky_teach_school_class s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 LEFT JOIN laky_teach_room r ON s.room_id=r.id AND r.the_status !=0 LEFT JOIN laky_teach_course c ON s.course_id=c.id AND c.the_status!=0 LEFT JOIN laky_organization_user u ON s.teacher_id =u.id AND u.the_status!=0 LEFT JOIN laky_organization_user u1 ON s.main_teacher_id = u1.id and u1.the_status!=0 where s.id =#{id,jdbcType=INTEGER} AND s.the_status !=0
AND s.school_zone_id IN
#{schoolId}

SELECT s.id AS id,s.end_date AS endDate,s.start_date AS startDate,s.name AS name,s.create_time AS createTime,s.remarks AS remarks,r.id as roomId, r.`name` AS roomName, c.`name` AS courseName,u1.id as mainTeacherId,u1.name AS mainTeacherName,u.`name` AS teacherName,sz.name as schoolZoneName from laky_teach_school_class s RIGHT JOIN laky_organization_school_zone sz on s.school_zone_id = sz.id and sz.the_status =1 LEFT JOIN laky_teach_room r ON s.room_id=r.id AND r.the_status !=0 LEFT JOIN laky_teach_course c ON s.course_id=c.id AND c.the_status!=0 LEFT JOIN laky_organization_user u ON s.teacher_id =u.id AND u.the_status!=0 LEFT JOIN laky_organization_user u1 ON s.main_teacher_id = u1.id and u1.the_status!=0 where s.the_status !=0
AND s.`name` LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')
AND s.school_zone_id IN
#{schoolId}
AND s.school_zone_id = #{schoolZoneId2,jdbcType=INTEGER}
AND s.school_zone_id IN
#{schoolId}
AND s.course_id = #{courseId,jdbcType=INTEGER}
ORDER BY s.create_time DESC
